import * as actionTypes from './actionTypes';
import axios from 'axios';

export const authStarted = () => {
	return {
		type: actionTypes.AUTH_START
	}
}

export const authSuccessed = (token) => {
	return {
		token: token,
		type: actionTypes.AUTH_SUCCESS
	}
}

export const authFailed = (error) => {
	return {
		error: error,
		type: actionTypes.AUTH_FAIL
	}
}

export const logouted = () => {
	localStorage.removeItem('user');
	localStorage.removeItem('expirationDate');
	return {
		type: actionTypes.AUTH_LOGOUT
	}
}

export const checkAuthTimeouted = expirationTime => {
	return dispatch => {
		setTimeout( () => {
			dispatch(logouted());
		}, expirationTime * 1000)
	}
}
export const authLogined = (username, password) => {
	return dispatch => {
		dispatch(authStarted());
		axios.post('http://127.0.0.1:8000/rest-auth/login/', {
			username: username,
			password: password
		}).then(res => {
			const token = res.data.key;
			const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
			localStorage.setItem('token', token);
			localStorage.setItem('expirationDate', expirationDate);
			localStorage.setItem('user', user);
			dispatch(authSuccessed(token));
			dispatch(checkAuthTimeouted(3600));
		})
		.catch(err => {
			dispatch(authFailed(err))
		})
	}
}

export const authSignuped = (username, email, password1, password2) => {
	return dispatch => {
		dispatch(authStarted());
		axios.post('http://127.0.0.1:8000/rest-auth/registration/', {
			username: username,
			email: email,
			password1: password1,
			password2: password2
		}).then(res => {
			const token = res.data.key;
			const expirationDate = new Date(new Date().getTime() + 3600 * 1000);
			localStorage.setItem('token', token);
			localStorage.setItem('expirationDate', expirationDate);
			dispatch(authSuccessed(token));
			dispatch(checkAuthTimeouted(3600));
		})
		.catch(err => {
			dispatch(authFailed(err))
		})
	}
}

export const authCheckedState = () => {
	return dispatch => {
		const token = localStorage.getItem('token');
		if (token === undefined) {
			dispatch(logouted());
		} else {
			const expirationDate = new Date(localStorage.getItem('expirationDate'));
			if (expirationDate <= new Date() ) {
				dispatch(logouted());
			} else {
				dispatch(authSuccessed(token));
				dispatch(checkAuthTimeouted( (expirationDate.getTime() - new Date().getTime()) / 1000 ));
			}

		}
	}	
}
