import React, {useReducer, Component} from 'react'
import {DateRangeInput} from '@datepicker-react/styled'

const initialState = {
  startDate: null,
  endDate: null,
  focusedInput: null,
}

function reducer(state, action) {
  switch (action.type) {
    case 'focusChange':
      return {...state, focusedInput: action.payload}
    case 'dateChange':
      return action.payload
    default:
      throw new Error()
  }
}

const [state, dispatch] = useReducer(reducer, initialState)

class App extends Component {

  render() {
	  return (
	    <DateRangeInput
	      onDatesChange={data => this.dispatch({type: 'dateChange', payload: data})}
	      onFocusChange={focusedInput => dispatch({type: 'focusChange', payload: focusedInput})}
	      startDate={this.state.startDate} // Date or null
	      endDate={this.state.endDate} // Date or null
	      focusedInput={this.state.focusedInput} // START_DATE, END_DATE or null
	    />
	  )
	}
}

export default App
