import React, {Component} from 'react';
import axios from "axios";
import { connect } from 'react-redux';
import * as actions from '../store/actions/actions';
import NavBar from '../Components/navBar'


class MyTrips extends Component {
	state = {
		tripIDs: [],
		trips: [],
		z: [1,2,3,4,5]
	}
	componentDidMount() {
		this.props.onTryAutoSignup();

		// Loads trips from backend
		axios.get("http://127.0.0.1:8000/api/users/1", {
			headers: {Authorization: "Token " + localStorage.token}
		}).then(res => {
			this.setState({tripIDs: res.data.trips});
			}).then(res => {
				for (const id of this.state.tripIDs) {
					console.log(id);
					axios.get("http://127.0.0.1:8000/api/trips/" + id, {
						headers: {Authorization: "Token " + localStorage.token}
					}).then(res => {
						const newTrips = {...this.state.trips};
						newTrips.push(res.data);
						this.setState({trips: newTrips});
					});
				}
			});
		console.log("eg", this.state.z[0]);
		console.log("kek0" ,this.state.trips.length);
		console.log("kek1" ,this.state.trips);



	}

	render() {
		console.log("trips", this.state.trips)
		return (
		<React.Fragment>
			<NavBar {...this.props}/>
			<div className = "jumbotron startBox">
				<h1> My Trips </h1>
				{
					(this.props.isAuthenticated) ? 
						<h1> 1 {this.props.username} </h1>

					: 
					<h1> FAILE </h1>
				}
			</div>

		</React.Fragment> 
		);
	}
}

const mapStateToProps = (state) => {
	return {
		// Basic stats needed for NAV ------------
		isAuthenticated: state.token !== null,
		username: state.username
		// Basic stats needed for NAV ------------
	}
}

const mapDispatchToProps = dispatch => {
	return {
		// Basic stats needed for NAV ------------
		onTryAutoSignup: () => dispatch(actions.authCheckedState()),
		logout: () => dispatch(actions.logouted())
		// Basic stats needed for NAV ------------
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(MyTrips);
