import React, {Component} from 'react';
import axios from "axios";
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import * as actions from './store/actions/auth';


class Login extends Component {
    // handleSubmit(event) {
    //   alert('Your favorite flavor is: ' + this.state.value);
    //   event.preventDefault();
    // }
    handleSubmit = (e) => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
          if (!err) {
            this.props.onAuth(values.username, values.password);
            this.props.history.push('/');
          }
        });
      }

    render() {
        return (
          <div className = "container-fluid align-items-center" id = "inputForm">
              <h1> Login</h1> 
              <div className = "jumbotron">
                  <form onSubmit={this.handleSubmit}>
                    <div>
                      <input value ="username" type = "text" name = "username" placeholder = "Enter Username" />
                    </div>

                    <div>
                      <input value ="password" type = "text" name = "password" placeholder = "Enter Password" />
                    </div>

                    <button type="submit" value = "Submit"> Submit </button>
                  </form>
              </div>
          </div>);

    }

}

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, password) => dispatch(actions.authLogin(username, password)) 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
