import React, {Component} from 'react';
import "bootstrap/dist/css/bootstrap.css";
import NavBar from '../Components/navBar';
import InputForm from '../Components/StartComponents/inputForm'
import "./CSS/StartPage.css"
import axios from "axios";

import {connect} from 'react-redux';
import * as actions from '../store/actions/actions'


class StartPage extends Component {
	state = { 
		articles: {}
	}

	componentDidMount() {
		this.props.onTryAutoSignup();
		const ID = this.props.match.params.inputID;
		console.log("ID", ID)
		axios.get(`http://127.0.0.1:8000/api/${ID}`)
			.then(res => {
				this.setState({articles: res.data});
				console.log("hello", this.state.articles);
			})
	}

	render() {
	  return (
	    <div >
	    	{
	    		 (this.props.isAuthenticated) ?
	    			 <h1> this.props.username hel</h1> : (<h1></h1>)
	    	}
	    	<NavBar {...this.props} />
	    	<InputForm articles={this.state.articles}/>
	    </div>
	  );
	}
}

const mapStateToProps = (state) => {
	return {
		isAuthenticated: state.token !== null,
		username: state.username
	}
}

const mapDispatchToProps = dispatch => {
	return {
		onTryAutoSignup: () => dispatch(actions.authCheckedState()),
		logout: () => dispatch(actions.logouted())
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(StartPage);
