import React, {Component} from 'react';
import axios from "axios";
import { connect } from 'react-redux';
import { NavLink } from 'react-router-dom';
import * as actions from './store/actions/auth';


class Signup extends Component {
   constructor(props) {
    super(props);
    this.state = {

    }
   }
    handleSubmit = (event) => {
        event.preventDefault();
        this.props.onAuth(event.target.username.value, event.target.email.value,
          event.target.password1.value, event.target.password2.value);
        this.props.history.push('/');
      }

    render() {
        return (
          <div className = "container-fluid align-items-center" id = "inputForm">
              <h1> Login</h1> 
              <div className = "jumbotron">
                  <form onSubmit={this.handleSubmit}>
                    <div>
                      <input type = "text" name = "username" placeholder = "Enter Username" />
                    </div>

                    <div>
                      <input type = "text" name = "email" placeholder = "Enter Email" />
                    </div>

                    <div>
                      <input type = "text" name = "password1" placeholder = "Enter Password" />
                    </div>

                    <div>
                      <input type = "text" name = "password2" placeholder = "Enter Password again" />
                    </div>

                    <button type="submit" value = "Submit"> Submit </button>
                  </form>
              </div>
          </div>);

    }

}

const mapStateToProps = (state) => {
    return {
        loading: state.loading,
        error: state.error
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (username, email, password1, password2) => dispatch(actions.authSignup(username, password)) 
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Signup);
