import React, {Component} from 'react';
import axios from "axios";
import { connect } from 'react-redux';
import * as actions from '../store/actions/actions';
import NavBar from '../Components/navBar'


class MyTrips extends Component {
	state = {
		tripIDs: [],
		trips: [],
		loading: true
	}
	componentDidMount() {

		//Get state from store
		this.props.onTryAutoSignup();

		//If user is not logged in, redirect to Login page
		if (localStorage.getItem('token') == null) {
			this.props.history.push({
					pathname: "/login",
					state: { from: this.props.location.pathname }
				});
		}

		// Loads trips from backend ASSUME LOGGED IN!!
		try {
			axios.get("http://127.0.0.1:8000/api/users/"+localStorage.userId, {
				headers: {Authorization: "Token " + localStorage.token}
			}).then(res => {
				this.setState({tripIDs: res.data.trips});
			}).then(res => {
				for (let id of this.state.tripIDs) {
					axios.get("http://127.0.0.1:8000/api/trips/" + id, 
					{
						headers: {Authorization: "Token " + localStorage.token}
					}).then( res => {
						this.setState(
							{trips: [...this.state.trips, res.data]}
							);
					});	
				}
				this.setState({loading: false});
			});
		} catch (error) {
			alert(error);
			this.props.history.push("/");
		}



	}
//stopped here
render() {
	if (this.state.loading) { return null };
	return (
		<React.Fragment>
		<NavBar {...this.props}/>
		<div className = "jumbotron startBox">
		<h1> My Trips </h1>
		{
			(this.state.trips.length > 0) ? 
			<ul>

			{this.state.trips.map((value,index) => <li key={index}> {value.tripName} </li>)}

			</ul>

			: 
			<p> No trips available </p>
		}
		</div>

		</React.Fragment> 
		);
}
}

const mapStateToProps = (state) => {
	return {
		// Basic stats needed for NAV ------------
		isAuthenticated: state.token !== null,
		username: state.username
		// Basic stats needed for NAV ------------
	}
}

const mapDispatchToProps = dispatch => {
	return {
		// Basic stats needed for NAV ------------
		onTryAutoSignup: () => dispatch(actions.authCheckedState()),
		logout: () => dispatch(actions.logouted())
		// Basic stats needed for NAV ------------
	}
}
export default connect(mapStateToProps, mapDispatchToProps)(MyTrips);
